"use client";

import { Button } from "@/registry/ui/button";
import { Field, FieldControl, FieldError, FieldLabel } from "@/registry/ui/field";
import { Form } from "@/registry/ui/form";
import { Input } from "@/registry/ui/input";
import * as React from "react";

export type IImage = {
  alt: string;
  src: string;
  views: number;
  downloads: number;
};

interface SignUpFormProps {
  image: IImage;
  onSuccess?: () => void;
}

export function SignUpForm({ image, onSuccess }: SignUpFormProps) {
  const [isLoading, setIsLoading] = React.useState(false);

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setIsLoading(true);

    setTimeout(() => {
      setIsLoading(false);
      if (onSuccess) onSuccess();
    }, 2000);
  };

  return (
    <div className="flex max-h-full">
      <div className="hidden w-1/3 lg:block relative">
        <img
          alt={image.alt}
          className="h-full w-full object-cover rounded-l-md"
          src={image.src}
        />
        <div className="absolute bottom-4 left-4 text-white/80 text-xs bg-black/20 p-2 rounded backdrop-blur-sm">
          Photo viewed {image.views.toLocaleString()} times
        </div>
      </div>

      <div className="flex w-full flex-col justify-center p-4 sm:p-8 lg:w-2/3">
        <div className="mx-auto w-full max-w-md space-y-8">
          <div className="text-center">
            <h1 className="text-4xl font-bold tracking-tight text-foreground">
              Join Us
            </h1>
            <p className="mt-2 text-muted-foreground">
              Already have an account? <Button variant={"link"}>Login</Button>
            </p>
          </div>

          <Form className="space-y-6" onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
              <Field>
                <FieldLabel>First name</FieldLabel>
                <FieldControl name="firstName" placeholder="" required />
                <FieldError />
              </Field>
              <Field>
                <FieldLabel>Last name</FieldLabel>
                <FieldControl name="lastName" placeholder="" required />
                <FieldError />
              </Field>
            </div>

            <Field>
              <FieldLabel>Email</FieldLabel>
              <FieldControl name="email" required type="email" />
              <FieldError />
            </Field>

            <Field>
              <FieldLabel className="flex gap-1">
                Username
                <span className="font-normal text-muted-foreground">
                  (only letters, numbers and underscores)
                </span>
              </FieldLabel>
              <FieldControl name="username" pattern="[a-zA-Z0-9_]+" required />
              <FieldError />
            </Field>

            <Field>
              <FieldLabel className="flex gap-1">
                Password
                <span className="font-normal text-muted-foreground">
                  (min. 8 char)
                </span>
              </FieldLabel>
              <Input minLength={8} name="password" required type="password" />
              <FieldError />
            </Field>

            <Button disabled={isLoading} type="submit">
              {isLoading ? "Joining..." : "Join"}
            </Button>
          </Form>

          <p className="text-center text-sm text-muted-foreground">
            By joining, you agree to the{" "}
            <Button className="px-0" variant={"link"}>
              Terms
            </Button>{" "}
            and{" "}
            <Button className="px-0" variant={"link"}>
              Privacy Policy
            </Button>
            .
          </p>
        </div>
      </div>
    </div>
  );
}

"use client";

import { Button } from "@/registry/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuGroupLabel,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/registry/ui/dropdown-menu";
import { toast } from "@/registry/ui/toast";
import {
  FacebookIcon,
  InstagramIcon,
  LinkIcon,
  ShareIcon,
  TwitterIcon,
} from "lucide-react";

export const ShareMenu = () => {
  const handleCopy = () => {
    navigator.clipboard.writeText("https://lumiui.dev");

    toast.success({
      title: "Copied",
    });
  };
  return (
    <DropdownMenu>
      <DropdownMenuTrigger
        className="w-20"
        render={
          <Button variant="outline">
            <ShareIcon className="size-4" /> Share
          </Button>
        }
      />
      <DropdownMenuContent align="end" matchAnchorWidth={false} side="top">
        <DropdownMenuGroup>
          <DropdownMenuGroupLabel>Socials</DropdownMenuGroupLabel>
          <DropdownMenuItem>
            <FacebookIcon className="size-4" /> <span>Facebook</span>
          </DropdownMenuItem>
          <DropdownMenuItem>
            <TwitterIcon className="size-4" /> <span>Twitter</span>
          </DropdownMenuItem>
          <DropdownMenuItem>
            <InstagramIcon className="size-4" /> <span>Instagram</span>
          </DropdownMenuItem>
        </DropdownMenuGroup>
        <DropdownMenuSeparator />
        <DropdownMenuItem onClick={handleCopy}>
          <LinkIcon className="size-4" /> Copy link
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
};

import type { DialogRootChangeEventDetails } from "@base-ui/react";
import { LoginForm } from "@/registry/blocks/dialog-02/components/login-form";
import { SignUpForm } from "@/registry/blocks/dialog-02/components/signup-form";
import type { IImage } from "@/registry/blocks/dialog-02/page";
import { Button } from "@/registry/ui/button";
import {
  createDialogHandle,
  Dialog,
  DialogBackdrop,
  DialogPopup,
  DialogPortal,
  DialogTrigger,
} from "@/registry/ui/dialog";
import { cn } from "@/registry/lib/utils";
import { toast } from "@/registry/ui/toast";
import {
  createTooltipHandle,
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/registry/ui/tooltip";
import { BookmarkIcon, PlusIcon } from "lucide-react";
import React from "react";

type UserAction = "bookmark" | "add-collection";
type FormType = "signin" | "signup";

type DialogPayload = {
  formType: FormType;
  action: UserAction;
  title: string;
};

const dialogHandle = createDialogHandle<DialogPayload>();
const tooltipHandle = createTooltipHandle<{ label: string }>();

export const TooltipGroup = ({ currentImage }: { currentImage: IImage }) => {
  const [isOpen, setIsOpen] = React.useState(false);
  const [triggerId, setTriggerId] = React.useState<string | null>(null);

  const handleOpenChange = (
    open: boolean,
    eventDetails: DialogRootChangeEventDetails,
  ) => {
    setIsOpen(open);
    if (eventDetails.trigger) {
      setTriggerId(eventDetails.trigger.id);
    }
  };

  const handleSuccess = (action: UserAction) => {
    setIsOpen(false);

    const messages: Record<UserAction, string> = {
      "add-collection": `Added ${currentImage.alt} to collection`,
      bookmark: `Bookmarked ${currentImage.alt}`,
    };

    toast.success({
      description: messages[action] || "Action completed successfully",
      title: "Success",
    });
  };

  return (
    <>
      <TooltipProvider>
        <div className="flex gap-2">
          <TooltipTrigger
            handle={tooltipHandle}
            id="trigger-bookmark"
            payload={{ label: "Bookmark this page" }}
            render={
              <DialogTrigger
                handle={dialogHandle}
                payload={{
                  action: "bookmark",
                  formType: "signin",
                  title: "Welcome Back",
                }}
                render={<Button size={"icon-sm"} variant={"outline"} />}
              >
                <BookmarkIcon aria-label="Bookmark" className="size-5" />
              </DialogTrigger>
            }
          />

          <TooltipTrigger
            handle={tooltipHandle}
            id="trigger-collection"
            payload={{ label: "Add to collection" }}
            render={
              <DialogTrigger
                handle={dialogHandle}
                payload={{
                  action: "add-collection",
                  formType: "signup",
                  title: "Create Account",
                }}
                render={<Button size={"icon-sm"} variant={"outline"} />}
              >
                <PlusIcon aria-label="Add to collection" className="size-5" />
              </DialogTrigger>
            }
          />

          <Tooltip handle={tooltipHandle}>
            {({ payload }) => (
              <TooltipContent showArrow={false} side="bottom" sideOffset={5}>
                <p>{payload?.label}</p>
              </TooltipContent>
            )}
          </Tooltip>
        </div>
      </TooltipProvider>

      <Dialog
        handle={dialogHandle}
        onOpenChange={handleOpenChange}
        open={isOpen}
        triggerId={triggerId}
      >
        {({ payload }) => {
          return (
            <DialogPortal>
              <DialogBackdrop
                className="backdrop-blur-none bg-black/40"
                forceRender
              />
              <DialogPopup
                className={cn(
                  "fixed left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 bg-background rounded-md shadow-md dark:shadow-xs w-full",
                  payload?.formType === "signin" && "max-w-md",
                  payload?.formType === "signup" && "max-w-2xl",
                )}
              >
                {payload?.formType === "signin" && (
                  <LoginForm onSuccess={() => handleSuccess(payload.action)} />
                )}
                {payload?.formType === "signup" && (
                  <SignUpForm
                    image={currentImage}
                    onSuccess={() => handleSuccess(payload.action)}
                  />
                )}
              </DialogPopup>
            </DialogPortal>
          );
        }}
      </Dialog>
    </>
  );
};

import { Avatar, AvatarFallback, AvatarImage } from "@/registry/ui/avatar";
import { Button } from "@/registry/ui/button";
import {
  PreviewCard,
  PreviewCardContent,
  PreviewCardTrigger,
