"use client";

import * as React from "react";
import { Slider as BaseSlider } from "@base-ui/react/slider";

import { cn } from "@/lib/utils";

function SliderRoot({
  className,
  ...props
}: React.ComponentProps<typeof BaseSlider.Root>) {
  return (
    <BaseSlider.Root
      className={cn("touch-none select-none", className)}
      data-slot="slider"
      {...props}
    />
  );
}

function SliderControl({
  className,
  ...props
}: React.ComponentProps<typeof BaseSlider.Control>) {
  return (
    <BaseSlider.Control
      className={cn("touch-none select-none", className)}
      data-slot="slider-control"
      {...props}
    />
  );
}

function SliderTrack({
  className,
  ...props
}: React.ComponentProps<typeof BaseSlider.Track>) {
  return (
    <BaseSlider.Track
      className={cn("rounded-full select-none", className)}
      data-slot="slider-track"
      {...props}
    />
  );
}

function SliderValue({
  className,
  ...props
}: React.ComponentProps<typeof BaseSlider.Value>) {
  return (
    <BaseSlider.Value
      className={cn("text-sm font-medium", className)}
      data-slot="slider-value"
      {...props}
    />
  );
}

function SliderIndicator({
  className,
  ...props
}: React.ComponentProps<typeof BaseSlider.Indicator>) {
  return (
    <BaseSlider.Indicator
      className={cn("select-none")}
      data-slot="slider-indicator"
      {...props}
    />
  );
}

function SliderThumb({
  className,
  ...props
}: React.ComponentProps<typeof BaseSlider.Thumb>) {
  return (
    <BaseSlider.Thumb
      className={cn(
        "disabled:pointer-events-none disabled:opacity-50",
        className,
      )}
      data-slot="slider-thumb"
      {...props}
    />
  );
}

function Slider({
  className,
  defaultValue,
  value,
  children,
  ...props
}: React.ComponentProps<typeof BaseSlider.Root>) {
  const thumbCount = React.useMemo(() => {
    const valueToInspect = value ?? defaultValue;
    if (Array.isArray(valueToInspect)) return valueToInspect.length;
    return 1;
  }, [value, defaultValue]);

  return (
    <BaseSlider.Root
      className="relative flex flex-col gap-2 w-full touch-none select-none data-[orientation=vertical]:h-full data-[orientation=vertical]:w-auto data-[orientation=vertical]:flex-col data-[disabled]:opacity-50"
      data-slot="slider"
      defaultValue={defaultValue}
      value={value}
      {...props}
    >
      <BaseSlider.Control
        data-slot="slider-control"
        className={cn(
          "flex w-full items-center py-2",
          "touch-none select-none",
          "data-[orientation=vertical]:h-full data-[orientation=vertical]:w-auto data-[orientation=vertical]:flex-col data-[orientation=vertical]:px-2 data-[orientation=vertical]:py-0",
          className,
        )}
      >
        <BaseSlider.Track
          data-slot="slider-track"
          className={cn(
            "bg-input relative h-2 w-full grow rounded-full select-none",
            "data-[orientation=vertical]:h-full data-[orientation=vertical]:w-2"
          )}
        >
          <BaseSlider.Indicator
            data-slot="slider-indicator"
            className={cn(
              "bg-primary/50 absolute h-full rounded-full select-none",
              "data-[orientation=vertical]:w-full",
            )}
          />
          {Array.from({ length: thumbCount }, (_, i) => (
            <BaseSlider.Thumb
              data-slot="slider-thumb"
              className={cn(
                "border-primary ring-ring/50 block size-4 shrink-0 rounded-full border bg-primary shadow-sm transition-[color,box-shadow] hover:ring-4 focus-visible:ring-4 focus-visible:outline-hidden disabled:pointer-events-none disabled:opacity-50",
                "has-[:focus-visible]:outline"
              )}
              index={i}
              key={i}
            />
          ))}
        </BaseSlider.Track>
      </BaseSlider.Control>
      {children}
    </BaseSlider.Root>
  );
}

export {
  SliderRoot,
  SliderControl,
  SliderTrack,
  SliderIndicator,
  SliderThumb,
  SliderValue,
  // Composite component
  Slider,
};
